const axios = require('axios');
const cleanResponse = (text) => {
return text
.replace(/\*\*(.*?)\*\*/g, '$1')
.replace(/__(.*?)__/g, '$1')
.replace(/\*(.*?)\*/g, '$1')
.replace(/_(.*?)_/g, '$1')
.replace(/`(.*?)`/g, '$1')
.replace(/\[(.*?)\]\(.*?\)/g, '$1')
.replace(/~~(.*?)~~/g, '$1')
.replace(/#+\s*(.*)/g, '$1')
.replace(/!\[.*?\]\(.*?\)/g, '')
.replace(/<[^>]+>/g, '')
.replace(/[ \t]+/g, ' ')
.replace(/\$@\$(.*?)\$@\$/g, '')
.trim();
};
